let output=document.querySelector("#output"),btn_key=document.querySelectorAll("#btn-key"),btn_clear=document.querySelector("#btn-key-clear"),btn_delete=document.querySelector("#btn-key-delete"),btn_equal=document.querySelector("#btn-key-equal");for(let btnValue of btn_key)btnValue.addEventListener("click",e=>{number=e.target.innerHTML,output.value+=number});btn_equal.addEventListener("click",(function(){try{output.value=eval(output.value)}catch(err){alert("Invalid"+err)}})),btn_clear.addEventListener("click",(function(){output.value=" "})),btn_delete.addEventListener("click",(function(){output.value=output.value.slice(0,-1)}));